    private void btnprosesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnprosesActionPerformed
        // TODO add your handling code here:
        
        // 1.Deklarasi Variable yang terdapat pada ID Peminjam, Status , Kode Buku 
        // Menggunakan tipe data String untuk pemprosesan lebih lanjut 
        String kodepeminjam,Status,nama;
        
        // Kode Buku dideklarasikan dalam bentuk string dari sebuah penginputan pada kode peminjam maka akan menghasilkan sebuah text.
        kodepeminjam = String.valueOf(txtkodepeminjam.getText());
        
        // Apabila di process menggunakan Huruf no Kapital : a001 maka output nya akan menghasilkan result : tidak terdaftar
        // Apabila di process menggunakan Huruf Kapital : A001 maka output nya akan menghasilkan result : Anggota 
        // setelah memasukan sebuah if condition jangan lupa memberikan input OR  (||)  
        //  apabila kita di minta untuk memilih pilihan benar maka kita menggunakan fungsi OR 
        if (kodepeminjam.equals("A001") ||   kodepeminjam.equals("a001")) {
            Status = "Members";
        }else 
            if (kodepeminjam.equals("B002") ||   kodepeminjam.equals("b002")) {
            Status = "Non Member";
        }else 
               if (kodepeminjam.equals("C003") ||   kodepeminjam.equals("c003")) {
            Status = "Staff";
        }else 
               if (kodepeminjam.equals("D005") ||   kodepeminjam.equals("d005")) {
            Status = "Dosen";
        }else 
             if (kodepeminjam.equals("E005") ||   kodepeminjam.equals("e005")) {
            Status = "Visitor";
        }else        
        {
                Status = "User Not Found, Please check again!";
        }
            txtstatus.setText(""+ Status);
        
    }//GEN-LAST:event_btnprosesActionPerformed

