    private void btnprosestotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnprosestotalActionPerformed
        // TODO add your handling code here:
        
        // Deklarasi Variable 
        String status;
        double Tarif,Total,Potongan;
          int Durasi;
          
          status  = String.valueOf(txtstatus.getText());
          Durasi = Integer.parseInt(txtDurasi.getText());
          Tarif = Double.parseDouble(txtTarif.getText());
          
          if (Durasi >= 10 &&  status.equals("Members")) {
                 Potongan = 0.1 * Tarif;
          } else
          if (Durasi >= 10 && status.equals("Dosen")) {
                  Potongan = 0.2 * Tarif;
                  }else{
               Potongan = 0;
                  }
           txtPotongan.setText(""+ Potongan);
           
             Total = (Durasi * Tarif) - Potongan;
             txttotal.setText(""+Total);
    }//GEN-LAST:event_btnprosestotalActionPerformed

